import React, { useState, useEffect } from 'react';
import more from './more.png';
import { Link } from 'react-router-dom';
import { supabase } from '../client';

const CommentCard = (props) => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const fetchCount = async () => {
      const { data, error } = await supabase.from('Posts').select('upvotes').eq('id', props.id);
      console.log('data:', data);
      console.log('error:', error);
      if (error) {
        console.error(error);
      } else if (data.length > 0) {
        setCount(data[0].upvotes);
      }
    };
    fetchCount();
  }, [props.id]);

  const updateCount = async () => {
    try {
      const { data, error } = await supabase.from('Posts').update({ upvotes: count + 1 }).eq('id', props.id);
      if (error) {
        console.error(error);
      } else if (data && data.length > 0) {
        setCount(data[0].upvotes);
      } else {
        setCount((count) => count + 1);
      }
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div className="Card">
    <Link to={'edit/' + props.id}>
        <img className="moreButton" alt="edit button" src={more} />
    </Link>

      <h2 className="title">{props.author}</h2>
      <p className="description">{props.description}</p>
    </div>
  );
};

export default CommentCard;
