import './App.css';
import Rule from './components/fight-club-rules.gif'
import AliGif from './components/ALI.gif'
import React from 'react';
import { useState, useEffect } from 'react'
import { useRoutes } from 'react-router-dom'
import ReadPosts from './pages/ReadPosts'
import CreatePost from './pages/CreatePost'
import EditPost from './pages/EditPost'
import { Link } from 'react-router-dom'
import { supabase } from './client'


const App = () => {


  const [posts, setPosts] = useState([0]);


  let element = useRoutes([
    {
      path: "/forum",
      element:<ReadPosts data={posts}/>
    },
    {
      path:"/edit/:id",
      element: <EditPost data={posts} />
    },
    {
      path:"/new",
      element: <CreatePost />
    }
  ]);

  useEffect(()=> {
    const fetchData = async () => {
      const { data } = await supabase
        .from("Posts")
        .select()
        .order("created_at", { ascending: true });


      setPosts(data);
    };
    fetchData();
  }, []) 

  return ( 

    <div className="App">

      <div className="header">
        <h1>FightHub ðŸ§¼</h1> 
        <h2> Are you interested in the world of sports? Specifically combat? Then look no further! FightHub is your online sancturary. But, you must remeber the golden rule...</h2>
        <img className="gif1" src={Rule} alt="" width={800} height={500}/>
        <h2> Feel free to discuss a range of topics concerning the world of combat sports! Anything from MMA to boxing, Muy Thai, and etc.</h2>
        <img className="gif1" src={AliGif} width={800} height={500}/>
        <Link to="/forum"><button className="headerBtn"> Explore Threads! </button></Link>
        <Link to="/new"><button className="headerBtn"> Upload Posts! </button></Link>
      </div>
        {element}
    </div>

  );
}

export default App;
